mapper.select:
SELECT EXISTS(
SELECT 1
FROM bookmark
WHERE user_id = #{userId}

    AND exchange_post_id = #{exchangePostId}


    AND event_post_id = #{eventPostId}

)

mapper.update:
UPDATE bookmark
SET folder_id = #{targetFolderId}
WHERE bookmark_id = #{bookmarkId}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM bookmark
WHERE bookmark_id = #{bookmarkId}
  AND user_id = #{userId}
