mapper.select:
SELECT p.exchange_post_id AS exchangePostId, p.user_id AS writer, u.nickname AS writerNickname, p.exchange_post_created_at AS exchangePostCreatedAt, p.post_trade_status AS postTradeStatus, p.exchange_post_title AS exchangePostTitle FROM exchange_post p JOIN users u ON p.user_id = u.user_id JOIN third_cate t ON p.third_cate_id = t.third_cate_id JOIN second_cate s ON t.second_cate_id = s.second_cate_id JOIN first_cate f ON s.first_cate_id = f.first_cate_id
AND s.second_cate_id IN
#{id}
AND t.third_cate_id IN
#{id}
ORDER BY p.exchange_post_created_at DESC
