mapper.select:
SELECT c.community_id AS communityId, c.user_id AS userId, c.comm_room_id AS roomId, c.content AS content, c.comm_created_at AS createdAt, u.nickname AS nickname, u.profile_image AS userProfile FROM community c JOIN users u ON u.user_id = c.user_id WHERE c.comm_room_id = #{roomId}
AND c.comm_created_at < #{before}
ORDER BY c.comm_created_at DESC LIMIT #{limit}
