mapper.select:
SELECT c.community_id AS communityId, c.user_id AS userId, c.comm_room_id AS commRoomId, c.type AS type, c.content AS content, c.comm_created_at AS commCreatedAt, u.nickname AS nickname, u.profile_image AS userProfile FROM community c JOIN users u ON u.user_id = c.user_id WHERE c.comm_room_id = #{roomId}
AND c.comm_created_at < #{before}
ORDER BY c.comm_created_at ASC LIMIT #{limit}

SELECT COUNT(*)
FROM community
WHERE user_id = #{userId}
  AND type = 'MEGAPHONE'
  AND EXTRACT(YEAR FROM comm_created_at) = #{date.year}
  AND EXTRACT(MONTH FROM comm_created_at) = #{date.monthValue}
