mapper.select:
SELECT n.notice_id, n.notice_title, n.notice_content, n.notice_created_at, n.is_popup FROM notice n WHERE 1=1
AND n.notice_title LIKE CONCAT('%', #{filter.keyword}, '%')
ORDER BY n.notice_created_at DESC OFFSET #{filter.offset} LIMIT #{filter.size}

SELECT n.notice_id, n.notice_title, n.notice_content, n.notice_created_at, n.is_popup
FROM notice n
WHERE n.notice_id = #{noticeId}
